mapper.select:
SELECT t.id, t.user_id AS userId, t.sms_id AS smsId, t.sms_time AS smsTime, t.push_time AS pushTime, t.`status`, t2.title, t2.content, t3.name AS userName, ( CASE WHEN (UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / (60 * 60 * 24) > 1 THEN CONCAT(FLOOR((UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / (60 * 60 * 24)),'天前') WHEN ((UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / (60 * 60) >= 1 AND (UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / (60 * 60 * 24) <= 1) THEN CONCAT(FLOOR((UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / (60 * 60)),'小时前') WHEN ((UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / (60 * 60) < 1 AND (UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / 60 >= 1) THEN CONCAT(FLOOR((UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / 60),'分前') WHEN ((UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) / 60 < 1 AND (UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) >= 1) THEN CONCAT(FLOOR(UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)),'秒前') WHEN ((UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) < 1 AND (UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(t.push_time)) >= 0) THEN '1秒前' ELSE '最近' END ) AS timeStr FROM t_sys_sms_record t INNER JOIN t_sys_sms t2 ON t2.id=t.sms_id INNER JOIN t_sys_user t3 ON t3.id = t.user_id WHERE t.`status` <> 0 and t.`status` <> 3
AND t.user_id=${userId}
ORDER BY t.push_time DESC LIMIT 0,5
